mapper.select:
SELECT 
FROM customer_orders 
WHERE order_no = #{orderNo} 
AND user_id = #{userId} 
AND deleted = 0

SELECT 
FROM customer_orders
WHERE user_id = #{userId}
AND deleted = 0

    AND create_time >= #{startDate}


    AND create_time <= #{endDate}

ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM customer_orders
WHERE user_id = #{userId}
AND deleted = 0

    AND create_time >= #{startDate}


    AND create_time <= #{endDate}
